import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';
import React, { Suspense, useState } from 'react'
import { Button, Card, CardActions, CardContent, CardMedia, ListItem, Typography } from '@material-ui/core';
import { Repository, RepositoryResponse } from '../data-objects/Repository';

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            backgroundColor: 'transparent',
            color: 'white',
            width: '100%',
        },
        main: {
            display: 'flex',
            justifyContent: 'center',
            flexWrap: 'wrap',
        },
        details: {
            flexGrow: 1,
            display: 'flex',
            minWidth: '70%'
        },
        cover: {
            alignSelf: 'center',
            justifySelf: 'center',
            width: 151,
            height: 151,
            minWidth: '10%',
            margin: 16,
        },
    }),
);


export default function PortfolioProject(props: Repository) {
    const classes = useStyles()
    const [downloadLink, setDownloadLink] = useState("")

    return (
        <ListItem>
            <Card className={classes.root} elevation={20}>
                <div className={classes.main}>
                    <div className={classes.details}>
                        <CardContent>
                            <Typography component="h5" variant="h5">
                                {props.name}
                            </Typography>

                            <Typography>
                                {props.description}
                            </Typography>
                        </CardContent>
                    </div>

                    <CardMedia
                        component="img"
                        className={classes.cover}
                        image={props.imageLink ? props.imageLink : ""}
                        title={props.name} />
                </div>

                <CardActions>
                    <Button
                        variant="outlined"
                        color="primary"
                        href={props.html_url}
                        target="_blank"
                        onClick={event => console.log(props.html_url)}>
                        Source code
                    </Button>

                </CardActions>
            </Card>
        </ListItem>

    );
}
